mapper.select:
SELECT * FROM read_status
WHERE chat_room_id = #{chatRoomId}
  AND user_id = #{userId}

mapper.update:
UPDATE read_status
SET is_read = #{isRead}
WHERE chat_room_id = #{chatRoomId} AND user_id = #{userId}

mapper.select:
SELECT COUNT(*) FROM read_status
WHERE chat_room_id = #{chatRoomId}
  AND user_id = #{userId}
  AND is_read = FALSE
